% Mutables functions don't exist
function A = mergeNaive(A)
    if length(A) > 1
        m = floor(length(A) / 2);

        L = A(1:m);
        R = A(m+1:end);

        L = mergeNaive(L);
        R = mergeNaive(R);

        i = 1;
        j = 1;
        k = 1;
        while i <= length(L) && j <= length(R)
            if L(i) <= R(j)
                A(k) = L(i);
                i = i + 1;
            else
                A(k) = R(j);
                j = j + 1;
            end
            k = k + 1;
        end

        while i <= length(L)
            A(k) = L(i);
            i = i + 1;
            k = k + 1;
        end

        while j <= length(R)
            A(k) = R(j);
            j = j + 1;
            k = k + 1;
        end
    end
end

